import { createSlice } from '@reduxjs/toolkit';

const cartSlice = createSlice({
    name: 'cart',
    initialState: {
        items: [],
        totalQuantity: 0,
    },
    reducers: {
        addItemToCart(state, action) {
            const newItem = action.payload;
            const exItem = state.items.find(item => item.id === newItem.id)
            state.totalQuantity++;
            if(!exItem){
                state.items.push({id: newItem.id, price: newItem.price, quantity: newItem.quantity, totalPrice: newItem.price, name: newItem.title});
            } else {
                exItem.quantity = exItem.quantity++;
                exItem.totalPrice = exItem.totalPrice + newItem.price;
            }
        },
        removeItemFromCart(state, action) {
            const id = action.payload;
            const exItem = state.items.find(item => item.id === id);
            state.totalQuantity--;
            if (exItem.quantity === 1) {
                state.items = state.items.filter(item => item.id!== id)
            } else {
                exItem.quantity--;
                exItem.totalPrice = exItem.totalPrice - exItem.price;
            }
        }
    }
})

export default cartSlice;
export const cartAction = cartSlice.actions;
